INSERT INTO `lt_feel` (`id`, `hrurl`, `order_num`, `price`, `name`, `description`, `level`, `duration`, `cat_id`, `warning`, `thanx`, `text`, `status`, `master_id`, `author`, `author_about`, `created_at`, `updated_at`) VALUES
(1, NULL, 1, 0, 'Радость жизни', 'Осознание радости в своей жизни', '2', '00:15:00', NULL, 'Упражнение требует спокойного состояния и отсутствия тревожащих факторов. \r\n\r\nДля оптимальных результатов необходимо полное погружение.', 'Надеемся, что погружение было глубоким и получена масса позитивных эмоций.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(2, NULL, 2, 0, 'Поиск призвания 1', 'Поиск жизненного призвания', '3', '00:20:00', NULL, 'Если в целом жизнь приносит удовлетворение, не беспокойся - ты на своем месте.\nА когда чего-то не хватает, всегда есть место для любимого хобби.\nТем не менее, мы всегда можем улучшить жизнь. Следующие упражнения помогут заглянуть в себя и узнать, что необходимо для достижения лучшего жизненного равновесия.', 'Надеемся, что упражнение было полезным.\nА в процессе его прохождения - найдены интересные мысли.\n\nУ нас есть еще много интересных и глубоких упражнений.', 'Смотри какая штуко\r\n1\r\n2\r\n3', NULL, NULL, NULL, NULL, NULL, 1515964134),
(3, NULL, 3, 1, 'Поиск призвания 2', 'Глубокая проработка, направленная на поиск жизненного призвания', '1', '01:00:00', NULL, 'Это упражнение серьезное.\n\nДля получения результата, необходимо полное непрерывное погружение. \n\nПродолжайте, если есть 30 минут свободного времени.', 'Надеемся, что упражнение было полезным.\nА в процессе его прохождения - найдены интересные мысли.\n\nДля лучшего глобального результата, желательно исключить прохождение других занятий сегодня. \n\nОбдумай полученные результаты и эмоции.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(4, NULL, 0, 0, 'Колесо жизни', 'Проработка сфер жизни.', '2', '00:15:00', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(5, NULL, 4, 0, 'Жажда перемен', 'Упражнение, направленное на понимание каких перемен ты хочешь в жизни.', '2', '00:20:00', NULL, 'Если в целом жизнь приносит удовлетворение, не беспокойся - ты на своем месте.\nА когда чего-то не хватает, всегда есть место для любимого хобби.\nТем не менее, мы всегда можем улучшить жизнь. Следующие упражнения помогут заглянуть в себя и узнать, что необходимо для достижения лучшего жизненного равновесия.', 'Надеемся, что упражнение было полезным.\nА в процессе его прохождения - найдены интересные мысли.\n\nУ нас есть еще много интересных и глубоких упражнений.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(6, NULL, 5, 1, 'Самоуважение', 'Упражнение для осознания и усиления уважения к себе.', '2', '00:30:00', NULL, 'Упражнение требует спокойного состояния и отсутствия тревожащих факторов. Для оптимальных результатов необходимо полное погружение.\n\nЧем серьезней ты относишься к выполнению задания, тем лучше и ярче результат.', 'Надеемся, что упражнение было полезным.\nА в процессе его прохождения - найдены интересные мысли.\n\nУ нас есть еще много интересных и глубоких упражнений.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);

