INSERT INTO `lt_feel_vars` (`id`, `feel_id`, `sort`, `question`, `example`, `created_at`) VALUES
(1, 1, 1, 'Найди исправную ручку и чистый лист бумаги.', '', NULL),
(2, 1, 2, 'Вспомни, что приносит тебе удовольствие в жизни.\r\n\r\nНапиши 10 пунктов.', '', NULL),
(4, 1, 3, 'Мысленно погрузись в каждое из написанных действий. \r\nНе спеши, представь во всех красках. \r\nПочувствуй свои эмоции относительно происходящего.', '', NULL),
(5, 1, 4, 'Вспомни 3 самых счастливых момента в своей жизни.\r\n\r\nЗапиши на обратной стороне листа бумаги.', '', NULL),
(6, 1, 5, 'Мысленно погрузись в каждое событие.', '', NULL),
(7, 1, 7, 'Найди одно слово, характеризующее эмоцию, вызываемую этим событием.\r\n\r\nЗапиши напротив каждого события на листе.', '', NULL),
(8, 2, 1, 'Найди  чистый лист бумаги (или тетрадь, блокнот) и исправную ручку.', '', NULL),
(9, NULL, NULL, 'Вспомни, какими занятиями тебе нравилось заниматься в своей жизни.\r\nНапротив каждого пункта напиши одну эмоцию, характеризующую для тебя это дело.', NULL, NULL),
(10, NULL, NULL, 'Какое дело по твоему мнению, тебе удается лучше всего?\r\nДелая что, ты получаешь максимальное внутреннее удовлетворение от результата?\r\n\r\nПодчеркни.', NULL, NULL),
(11, NULL, NULL, 'Оцени уровень развития твоей жизни в области семьи и личной жизни. \r\nПоставь цифру от 1 до 10 в качестве оценки', NULL, NULL),
(12, NULL, NULL, 'Оцени уровень развития твоей жизни в области друзей и окружения\r\nПоставь цифру от 1 до 10 в качестве оценки', NULL, NULL),
(13, NULL, NULL, 'Оцени уровень развития твоей жизни в области здоровья и спорта\r\nПоставь цифру от 1 до 10 в качестве оценки', NULL, NULL),
(14, NULL, NULL, 'Оцени уровень развития твоей жизни в области развлечений и яркости жизни\r\nНасколько часто ты развлекаешься и весело живешь?\r\nПоставь цифру от 1 до 10 в качестве оценки', NULL, NULL),
(15, NULL, NULL, 'Оцени уровень развития твоей жизни в области творчества и духовности\r\nТы творческий человек?\r\nПоставь цифру от 1 до 10 в качестве оценки', NULL, NULL),
(16, NULL, NULL, 'Оцени уровень развития твоей жизни в области бизнеса и карьеры\r\nУспех тебе сопутствует?\r\nПоставь цифру от 1 до 10 в качестве оценки', NULL, NULL),
(17, NULL, NULL, 'Оцени уровень развития твоей жизни в области финансов\r\nТебе на жизнь хватает?\r\nПоставь цифру от 1 до 10 в качестве оценки', NULL, NULL),
(18, NULL, NULL, 'Оцени уровень развития твоей жизни в области личностного роста\r\nТы развиваешься?\r\nПоставь цифру от 1 до 10 в качестве оценки', NULL, NULL),
(19, NULL, NULL, 'Взгляни на твою оценку своей жизни.\r\nКакие сферы ты считаешь можно подтянуть для сбалансированного развития?\r\nКакими способами можно усилить слабые сферы?\r\nНапиши 3 способа развития каждой слабой сферы и отправь себе на e-mail', NULL, NULL),
(20, NULL, NULL, 'Ради каких дел ты с удовольствием просыпался бы с утра?\r\n\r\nПодчеркни.\r\n(если это занятие уже подчеркнуто, подчеркни второй линией ниже)', NULL, NULL),
(21, NULL, NULL, 'Взгляни на записанную информацию.\r\nКакие тенденции просматриваются?\r\n\r\nВыпиши 1-3 основных мысли.', NULL, NULL),
(22, NULL, NULL, 'Оцени, насколько выписанные тенденции важны для тебя.\r\nДостаточное ли место они занимают в твоей настоящей жизни?', NULL, NULL),
(23, NULL, NULL, 'Найди 2-3 чистых листа бумаги (или тетрадь, блокнот) и исправную ручку.', NULL, NULL),
(24, NULL, NULL, 'Какие дела тебя раздражают, что не нравится делать? \r\nЗапиши на листе 1-3 занятий.', NULL, NULL),
(25, NULL, NULL, 'Напротив каждого пункта напиши, что именно в этом действии выводит тебя из равновесия.', NULL, NULL),
(26, NULL, NULL, 'Зачеркни каждый из записанных пунктов. \r\nОставь только причину - то что раздражает.', NULL, NULL),
(27, NULL, NULL, 'Какие эмоции тебе (в идеале) хотелось бы испытывать при работе? \r\nЧто бы тебе хотелось испытывать, что бы начинать новый рабочий день с удовольствием?\r\n\r\nЗапиши 3-5 пунктов.', NULL, NULL),
(28, NULL, NULL, 'Какое дело по твоему мнению тебе удается лучше всего?\r\nДелая что, ты получаешь максимальное внутреннее удовлетворение от результата?\r\n\r\nЗапиши это дело.', NULL, NULL),
(29, NULL, NULL, 'Вернись в молодость, в переходный период, 13-18 лет. \r\nЭто замечательное время, когда ты уже представляешь себе как устроен мир, но еще нет ограничений типа - “А что если у меня не получится\", и т. д. \r\nЧем ты увлекаешься? \r\nКакие у тебя собственные мечты, не навязанные взрослыми?\r\n\r\nЗапиши на бумаге одно или несколько занятий, которыми мечтаешь заниматься, когда вырастешь.', NULL, NULL),
(30, NULL, NULL, 'Возвращаемся в настоящее время. \r\nЧто ты любишь делать (кроме сна и просмотра любимых сериалов)?\r\n\r\nНапиши список из 5-10 занятий, которые приносят тебе удовольствие в этот период жизни.\r\nВ список могут быть включены активные виды отдыха и спорт.\r\nНапротив каждого пункта напиши одну эмоцию, характеризующую для тебя это дело.', NULL, NULL),
(31, NULL, NULL, 'Вспомни, какими занятиями тебе нравилось заниматься ранее в своей жизни.\r\n\r\nЕсли есть такие, которые не входят ни в один из записанных уже списков - напиши их отдельно.\r\n\r\nНапротив каждого пункта напиши одну эмоцию, характеризующую для тебя это дело.', NULL, NULL),
(32, NULL, NULL, 'Иногда делая что-то, ты настолько полностью погружаешься в это процесс, что забываешь о течении времени.\r\n\r\nЧто это за дело? Какое из описанных занятий наиболее подходит под такое определение?\r\n\r\nПодчеркни.', NULL, NULL),
(33, NULL, NULL, 'Ради каких дел ты с удовольствием просыпался бы с утра?\r\n\r\nПодчеркни. \r\n(если это занятие уже подчеркнуто, подчеркни второй линией ниже)', NULL, NULL),
(34, NULL, NULL, 'Представь себе людей с которыми тебе было бы комфортно общаться. \r\n\r\nЭто люди аналитического склада или творческого? \r\nОбщительны или молчаливы? \r\nКак их можно охарактеризовать? \r\nЧто помогает, заряжает и вдохновляет тебя в таком общении?\r\n\r\nЗапиши:\r\n1) Род деятельности этих людей.\r\n2) Что их объединяет?\r\n3) Почему тебе хорошо с ними? \r\n4) Чем тебе помогает это общение?', NULL, NULL),
(35, NULL, NULL, 'Взгляни на записанную информацию.\r\nКакие тенденции просматриваются?\r\n\r\nВыпиши 1-3 основных мысли.', NULL, NULL),
(36, NULL, NULL, 'Оцени, насколько выписанные тенденции важны для тебя.\r\nДостаточное ли место они занимают в твоей настоящей жизни?\r\nИмеет ли смысл развить какие либо из твоих навыков дополнительным обучением?\r\nНеобходимо ли уделить чему-то определенное время, если оно неоправданно забыто?\r\n\r\nЗапиши основной вывод из проделанного упражнения и отправь себе на e-mail, что бы переосмыслить на свежую голову.', NULL, NULL),
(37, NULL, NULL, 'Найди  чистый лист бумаги (или тетрадь, блокнот) и исправную ручку.', NULL, NULL),
(38, NULL, NULL, 'Что ты хочешь изменить в своей жизни?\r\n\r\nНапиши 5 пунктов.', NULL, NULL),
(39, NULL, NULL, 'По каждому пункту напиши 3 варианта решения. ', NULL, NULL),
(40, NULL, NULL, 'Представь что эти перемены уже произошли. \r\nКак это отразилось на остальных сферах жизни?\r\nКак это отразилось на близких тебе людях?', NULL, NULL),
(41, NULL, NULL, 'Сделай выводы из проделанного упражнения.', NULL, NULL),
(42, NULL, NULL, 'Найди  чистый лист бумаги (или тетрадь, блокнот) и исправную ручку.', NULL, NULL),
(43, NULL, NULL, 'За что тебя ценят люди?\r\n\r\nВыпиши 5-10 пунктов.', NULL, NULL),
(44, NULL, NULL, 'Какой из пунктов ты считаешь наиболее важным?  \r\n\r\nПодчеркни.', NULL, NULL),
(45, NULL, NULL, 'Что ты умеешь делать лучше всего?\r\n\r\nВыпиши 3-10 пунктов.', NULL, NULL),
(46, NULL, NULL, 'Какой из навыков ты считаешь наиболее важным?  \r\n\r\nПодчеркни.', NULL, NULL),
(47, NULL, NULL, 'Чего ты добился в своей жизни?\r\n\r\nВыпиши 1-5 пунктов.', NULL, NULL),
(48, NULL, NULL, 'Чего ты еще планируешь добиться в жизни?\r\n\r\nВыпиши 1-5 пунктов.', NULL, NULL),
(49, NULL, NULL, 'Подчеркни: \r\n\r\nсамое важное достижение прошлого \r\n\r\nсамую важную цель в будущем', NULL, NULL),
(50, NULL, NULL, 'Взгляни на записанную информацию.\r\nОбрати внимание на все подчеркнутые пункты, постарайся их запомнить.', NULL, NULL),
(51, NULL, NULL, 'Последующие действия поместят тебя в определенную позу. \r\n\r\nДействия не должны вызывать дискомфорт. \r\n\r\nПри появлении дискомфорта, ослабь вызвавшее его движение.', NULL, NULL),
(52, NULL, NULL, 'Встань прямо, опираясь на одну ногу, поставь другую на что-либо высотой 20-30см. \r\n\r\nРуки согни, ладони на пояс (положение “Руки в боки”). \r\n\r\nВыпрями спину максимально прямо (положение “Грудь колесом”).\r\n\r\nПриподними подбородок.\r\n\r\nЗакрой глаза и закрытыми глазами посмотри чуть выше горизонта (там где обычно находится утром солнце).\r\n\r\nВспомни выделенные пункты.\r\n\r\nНаполнись уверенностью и энергией, почувствуй себя хозяином своей жизни. \r\n\r\nВ этой позе проведи 1-5 минут.', NULL, NULL);

