INSERT INTO `quote` (`id`, `list_num`, `image`, `image_alt`, `text`, `addition`, `author`, `master_id`, `status`) VALUES
(1, NULL, 'q_rollo_may2.jpg', '', 'Основная цель психотерапии -\r\nосвободить людей', '', 'Ролло Мей', NULL, ''),
(2, NULL, 'selfrespect5.jpg', NULL, 'Я не результат обстоятельств,\nя результат моих решений. ', '', '', NULL, ''),
(3, NULL, 'q_sigmund_freud.jpg', NULL, 'Невыраженные эмоции никогда не умрут.\r\nОни погребены заживо и вернутся позже в более ужасном виде. ', '', 'Зигмунд Фрейд', NULL, ''),
